(: set the value of the variable $x to 1234 and then print out $x :)
let $x := 12 return (xdmp:set($x, 1234), $x)
=> 1234

(: set the value of the variable $x to 5 and then print out $x for each value of $y :)
for $x in (1, 2) for $y in ($x, $x) return ($y, xdmp:set($x, 5), $x)
=> (1, 5, 1, 5, 2, 5, 2, 5)

(: note the effect on $z of changing the
   value of $x :)

for $x in (1, 2)
  for $y in (3,4)
    for $z in ($x, $x)
return
($z, xdmp:set($x, 5))

=> (1, 1, 5, 5, 2, 2, 5, 5)

(: every time the name of the input node changes,
   output the new name  :)

let $n := ()
for $i in (<x>1</x>, <x>2</x>, <y>3</y>)
return (
   if (name($i) eq $n)
   then ()
   else (xdmp:set($n, name($i)), $n)
  , data($i)
)

=> (x, 1, 2, y, 3)

