// serialize a JSON document in the database to a file on disk 
var json = fn.doc("/mydocs/example.json");
for (var x of json) {
xdmp.save("/space/hello.txt", x);
};

// save a text file 
var textnode = xdmp.toJSON("hello");
xdmp.save("/space/hello.txt", textnode);

// save a text document stored in the database to 
// disk, explicitly specifying the output encoding 
var txt = fn.doc("/mydocs/stuff.txt");
var options = {"outputEncoding": "utf-8"};
for (var x of txt) {
xdmp.save("mystuff.txt", x, options);
};

declareUpdate();
xdmp.documentLoad("c:\\myFile.json",
    {
      "uri" : "/documents/myFile.json",
      "permissions" : xdmp.defaultPermissions()
    })

=> Loads the document with a URI "/documents/myFile.json".

declareUpdate();
xdmp.documentLoad("http://myCompany.com/file.json",
    {
      "uri" : "/documents/myFile.json",
      "permissions" : xdmp.defaultPermissions(),
      "format" : "json",
      "authentication" : {
        "username" : "user",
        "password" : "pass"
      }
    })

=> Loads the document with a URI "/documents/myFile.json"
   from the server http://myCompany.com, sending the
   credentials user/pass. The document is loaded as JSON.

xquery version "1.0-ml";
import module namespace temporal = "http://marklogic.com/xdmp/temporal" at "/MarkLogic/temporal.xqy";
temporal:document-load("temporalCollection", "c:\myFile.xml", <options xmlns="temporal:document-load"> <uri>/documents/myFile.xml</uri> <repair>none</repair> <permissions>{xdmp:default-permissions()}</permissions> </options>)
=> Loads the temporal document with a URI "/documents/myFile.xml" into the temporal collection, "temporalCollection", and does not perform tag repair during the load.

